package ch01;

public class IncreaseDecreaseExample3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1;i<=10;i++) {// 반복문의 마지막에 있는 실행문 i++ 는 증가 연산
			System.out.println(i);
		}
		
		for(int j=10;j>=1;j--) {//j--는 감소 연산
			System.out.println(j);
		}
		
		
	}

}
