package ch01;

public class StringOpExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10;
		a+=5; // +啊 歹窍扁 楷魂, 搬苞 15 
		//a=10 -> a=15
		
	   //曼炼鸥涝	
       String str ="hello";//"hello"按眉 积己
       
       str+="hi"; //"hellohi" 按眉 积己, "hi"按眉 积己,
       str+="bye";//"hellohibye" 按眉 积己, "bye" 按眉 积己
       System.out.println(str);
		
		
	}
}
